import styled from "styled-components";

const Card = styled.div`
  border: 1px solid #efefef;
  background: #fff;
  display: table;
  clear: both;
`;

const CheckBox = styled.div`
  float: left;
  margin-left: 20px;
  margin-right: 20px;
`;

const ListItem = styled.div`
  float: left;
  width: 80%;
  .input {
    width: 100%;
  }
`;
const Tag = styled.div`
  float: left;
  margin-left: 20px;
  margin-right: 20px;
`;
const SubmitBtn = styled.div`
  float: left;
  margin-left: 20px;
  margin-right: 20px;
`;

